CREATE SEQUENCE issue_seq INCREMENT BY 1 MINVALUE 1 MAXVALUE 9223372036854775807 START 511122 NO CYCLE;;
CREATE TABLE secoes(id VARCHAR PRIMARY KEY, turno INTEGER DEFAULT(1), uf VARCHAR, regiao VARCHAR, municipio VARCHAR, zona VARCHAR, secao VARCHAR, modelo_urna VARCHAR, tipo_urna VARCHAR, versao_sw VARCHAR, fuso_offset INTEGER, emissao VARCHAR, eleitores_aptos INTEGER, comparecimento INTEGER, lib_codigo INTEGER, comp_biometrico INTEGER, pct_biometria FLOAT, pct_abstencao FLOAT, hora_abertura VARCHAR, hora_encerramento VARCHAR, duracao_min INTEGER, reboots INTEGER DEFAULT(0), erros_log INTEGER DEFAULT(0), alertas_mesario INTEGER DEFAULT(0), votos_log INTEGER DEFAULT(0), substituicoes INTEGER DEFAULT(0), is_reserva BOOLEAN DEFAULT(CAST('f' AS BOOLEAN)), has_issues BOOLEAN DEFAULT(CAST('f' AS BOOLEAN)), n_issues INTEGER DEFAULT(0));;
CREATE TABLE issues(id INTEGER, secao_id VARCHAR, codigo VARCHAR, severidade VARCHAR, descricao VARCHAR, base_legal VARCHAR, detalhes VARCHAR, FOREIGN KEY (secao_id) REFERENCES secoes(id));;
CREATE TABLE totais_cargo(secao_id VARCHAR, eleicao_id INTEGER, cargo VARCHAR, codigo_cargo INTEGER, comparecimento INTEGER, nominais INTEGER, brancos INTEGER, nulos INTEGER, legenda INTEGER, total INTEGER, FOREIGN KEY (secao_id) REFERENCES secoes(id));;
CREATE TABLE votos(secao_id VARCHAR, eleicao_id INTEGER, cargo VARCHAR, codigo_cargo INTEGER, tipo_voto VARCHAR, codigo_candidato INTEGER, partido INTEGER, quantidade INTEGER, FOREIGN KEY (secao_id) REFERENCES secoes(id));;

